
pub fn main() {


    let stream = "";
    // let stream ="mjqjpqmgbljsphdztnvjfqwrcgsmlb";
    // solution 1 -> packet range for 4 this probably tests you loop time complexity
    let packet_range = 14;
    let mut mychar = 0;
    let looper:Vec<char> = stream.chars().collect();
    for (k,_) in looper[0..looper.len()-packet_range].iter().enumerate(){
        let currentpacket = looper[k..k+packet_range].to_vec();

        // stole this -> from the current i check if slice contains the previous one and do that for all items
        if (1..currentpacket.len()).any(|i| currentpacket[i..].contains(&currentpacket[i - 1])){
            continue;
        } else {
            mychar=k+packet_range;
            break;
        }
    }

    println!("{}",mychar);

}